mapper.delete:
DELETE FROM MYLIKE
WHERE EMAIL = #{email}
AND SUGGESTBBSSEQ = #{suggestbbsseq}

mapper.select:
SELECT SM.* FROM (SELECT ROWNUM as SI,SV.* FROM (SELECT SBS.*, CASE WHEN CNT.LIKECNT IS NULL THEN 0 ELSE CNT.LIKECNT END AS LIKECNT, CASE WHEN ML.SUGGESTBBSSEQ IS NULL THEN 0 ELSE 1 END AS LIKECHECK, (SELECT COUNT(*) FROM SUGGESTBBS WHERE SUGGESTBBSDEL = 0
AND SUGGESTBBSCATEGORY = #{category}
AND SUGGESTBBSTITLE LIKE '%'||#{search}||'%'
) as TOTALCNT FROM SUGGESTBBS SBS LEFT JOIN (SELECT SUGGESTBBSSEQ,COUNT(*) AS LIKECNT FROM MYLIKE GROUP BY SUGGESTBBSSEQ) CNT ON SBS.SUGGESTBBSSEQ = CNT.SUGGESTBBSSEQ LEFT JOIN (SELECT SUGGESTBBSSEQ FROM MYLIKE WHERE EMAIL = #{email}) ML ON SBS.SUGGESTBBSSEQ = ML.SUGGESTBBSSEQ WHERE SBS.SUGGESTBBSDEL = 0
AND SBS.SUGGESTBBSCATEGORY = #{category}
AND SBS.SUGGESTBBSTITLE LIKE '%'||#{search}||'%'
ORDER BY ${sortdate}
ORDER BY SBS.SUGGESTBBSSEQ
) SV )SM WHERE SM.SI > #{startPage} AND #{endPage} > SM.SI

SELECT NVL(COUNT(*), 0) FROM SUGGESTBBS WHERE 1=1 AND SUGGESTBBSDEL = 0
AND SUGGESTBBSCATEGORY = #{category}
AND SUGGESTBBSTITLE LIKE '%'||#{search}||'%'
