mapper.select:
SELECT CHALLENGESEQ, CATEGORY, CHALLENGETITLE, CHALLENGETEXT, CHALLENGESTART, CHALLENGEEND, CHALLENGEPERIOD, IDENTIFYFREQUENCY, IDENTIFYDAY, IDENTIFYTIME, CHALLENGEPHOTO, CHALLENGESAVEPHOTO, RATINGAVG, HEADERNUM, POINTCOUNT, CHALLENGEMEMBER, EMAIL, NICKNAME, AUTH, MEMBERPHOTO FROM (SELECT ROW_NUMBER()OVER(ORDER BY CHALLENGEMEMBER DESC) AS RNUM, CHALLENGESEQ, CATEGORY, CHALLENGETITLE, CHALLENGETEXT, CHALLENGESTART, CHALLENGEEND, CHALLENGEPERIOD, IDENTIFYFREQUENCY, IDENTIFYDAY, IDENTIFYTIME, CHALLENGEPHOTO, CHALLENGESAVEPHOTO, RATINGAVG, HEADERNUM, POINTCOUNT, CHALLENGEMEMBER, C.EMAIL, M.NICKNAME, M.AUTH, M.MEMBERPHOTO FROM CHALLENGE C, MEMBER M WHERE 1=1 AND C.EMAIL = M.EMAIL AND CHALLENGESTOP = 0 AND CHALLENGEDEL = 0
AND CATEGORY = ${category}
AND CHALLENGETITLE LIKE '%'||#{search}||'%'
ORDER BY CHALLENGEMEMBER DESC, CHALLENGESTART DESC) WHERE RNUM BETWEEN ${startPage} AND ${endPage}

SELECT COUNT(*) FROM CHALLENGE WHERE 1=1 AND CHALLENGESTOP = 0 AND CHALLENGEDEL = 0
AND CATEGORY = ${category}
AND CHALLENGETITLE LIKE '%'||#{search}||'%'
